import React, { useState } from "react";
import { ethers } from "ethers";
import contractAbi from "./contract-abi.json";

// Please replace with your actual contract address
const contractAddress = "0xA84F344aC9fB0D6cc5463E05986b63c1B949Cae2";

function App() {
  const [name, setName] = useState("");
  const [sent, setSent] = useState(null);
  const [recieved, setRecieved] = useState(null);
  const [buyer, setBuyer] = useState("");

  async function fetchContractInfo() {
    console.log("Button clicked!");
  
    if (!window.ethereum) {
      alert("Please install MetaMask!");
      return;
    }
  
    // 檢查網路
    const chainId = await window.ethereum.request({ method: 'eth_chainId' });
    console.log("Detected chain ID:", chainId); // Sepolia 是 0xaa36a7 (hex), 11155111 (dec)
    
    const provider = new ethers.providers.Web3Provider(window.ethereum);
  
    // 檢查 contract address 與 ABI 狀態
    console.log("Contract address:", contractAddress);
    console.log("ABI length:", contractAbi.length);
  
    // print contract object
    const contract = new ethers.Contract(contractAddress, contractAbi, provider);
    console.log("Contract object:", contract);
  
    try {
      const contractName = await contract.getName();
      setName(contractName);
      console.log("Fetched name:", contractName);
  
      const sent = await contract.isSent();
      setSent(sent);
      console.log("isSent:", sent);
  
      const recieved = await contract.isRecieved();
      setRecieved(recieved);
      console.log("isRecieved:", recieved);
  
      try {
        const buyerAddr = await contract.getBuyer();
        setBuyer(buyerAddr);
        console.log("Buyer address:", buyerAddr);
      } catch (e) {
        setBuyer("Not sold yet");
        console.log("getBuyer() error:", e);
      }
    } catch (e) {
      console.error("Contract call error:", e);
      alert("Error calling contract: " + (e && e.message ? e.message : e));
    }
  }

  return (
    <div style={{ padding: 30 }}>
      <h2>Smart Contract Details</h2>
      <button onClick={fetchContractInfo}>View Contract Info</button>
      <div>
        <p>Item Name: {name}</p>
        <p>Sent: {sent === null ? "" : sent ? "✅" : "❌"}</p>
        <p>Received: {recieved === null ? "" : recieved ? "✅" : "❌"}</p>
        <p>Buyer Address: {buyer}</p>
      </div>
    </div>
  );
}

export default App;
