import React, { useState } from "react";
import { ethers } from "ethers";
import contractAbi from "./contract-abi.json";

// 請替換下方合約位址
const contractAddress = "YOUR_CONTRACT_ADDRESS_HERE";

function App() {
  const [name, setName] = useState("");
  const [sent, setSent] = useState(null);
  const [recieved, setRecieved] = useState(null);
  const [buyer, setBuyer] = useState("");

  async function fetchContractInfo() {
    // 與 Metamask 互動
    if (typeof window.ethereum === "undefined") {
      alert("請先安裝 MetaMask，並選擇正確網路！");
      return;
    }
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const contract = new ethers.Contract(contractAddress, contractAbi, provider);
    setName(await contract.getName());
    setSent(await contract.isSent());
    setRecieved(await contract.isRecieved());
    try {
      setBuyer(await contract.getBuyer());
    } catch (e) {
      setBuyer("尚未成功拍賣");
    }
  }

  return (
    <div style={{ padding: 30 }}>
      <h2>智能合約查詢</h2>
      <button onClick={fetchContractInfo}>查詢合約資訊</button>
      <div>
        <p>商品名稱：{name}</p>
        <p>是否已寄出：{sent === null ? "" : sent ? "✅" : "❌"}</p>
        <p>是否已收到：{recieved === null ? "" : recieved ? "✅" : "❌"}</p>
        <p>買家地址：{buyer}</p>
      </div>
    </div>
  );
}

export default App;
